<?php

//arquivos com configurações das variáveis da página STORE

//define um "diretório" para o php, podendo assim existir uma class home diferente em outro namespace
namespace App\Controller\Pages;

use \App\Utils\View;
use \App\Model\Entity\Organization;

class Store extends Pages
{

    private static function getEvent()
    {
        $obOrganization = new Organization;
        $posts = $obOrganization->db_methods('GET', 'events');

        $length = count($posts);
        $str = '';



        for ($i = 0; $i <= $length - 1; $i++) {

            $name = $posts[$i]['name'];
            $description = $posts[$i]['description'];
            $id = $posts[$i]['id'];
            $value = $posts[$i]['value'];

            $htmlPost = '
            <div class="card" style="position: static; box-shadow: 1px 2px 15px black; margin: 0 0 2vh 0">
                <div class="card-title" style=" text-align: center; padding-top: 2vh;">
                    <h5 class="text-success" id="titleEvent">' . $name . '</h5>
                    <p hidden id="valueEvent">' . $value . '<p>
                    
                </div>
                <hr>
                <div class="card-body" id="descriptionEvent' . $id . '">
                    <p style="text-aling:center;">' . $description . '</p>
                    <br>

                    <div id="payEvent' . $id . '" style="text-align: center;">
                        <button class="btn btn-success" onclick="eventQrCode()">Comprar</button>
                    </div>
                </div>
                <div class="card-footer" id="qrCodeEvent"></div>
            </div>';

            $str .= $htmlPost;
        }

        return $str;
    }

    /**
     * Método responsável por retornar o conteúdo (view) da nossa Home;
     * @return string;
     */
    public static function getStore()
    {

        #$obOrganization = new Organization;

        //Acessa a classe View e realiza o render da home, retornando a resposta da func. render($arg)
        $pageContent =  View::render('pages/store', [
            'monthEvent' => self::getEvent()
        ]);

        $img =    'data:image/jpeg;base64,';

        return self::getPages('Jovens Peregrinos', $img, $pageContent);
    }
}
